import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../api/api';
import { toast } from 'react-toastify';
import DeleteConfirmationModal from './DeleteConfirmationModal';

function ProjectCard({ project, onDelete }) {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const navigate = useNavigate();
  const user = JSON.parse(localStorage.getItem('user'));

  const handleClick = () => {
    navigate('/project-details', { state: project });
  };

  const handleDelete = async () => {
    try {
      // Update the `delete` field to true on the server
      const updatedProject = { ...project, delete: true };
      await api.put(`/projects/${project.title}`, updatedProject); // Assuming a PUT endpoint exists
      toast.success(`Project "${project.title}" has been marked as deleted.`);

      // Call the parent component's onDelete function to update the UI
      if (onDelete) {
        onDelete(project.title);
      }
      setIsModalOpen(false); // Close the modal after successful deletion
    } catch (error) {
      console.error('Error deleting project:', error);
      toast.error('Failed to delete the project. Please try again.');
    }
  };

  return (
    <div
      className="relative bg-white shadow-md rounded-lg p-4 mb-6 cursor-pointer hover:shadow-lg transition-shadow"
      onClick={handleClick}
    >
      {/* Delete Button for Admins */}
      {user?.role === 'admin' && (
        <button
          onClick={(e) => {
            e.stopPropagation();
            setIsModalOpen(true); // Open the modal
          }}
          className="absolute top-2 right-2 text-red-600 hover:text-red-800 transition-colors"
          title="Delete Project"
        >
          Delete
        </button>
      )}

      <div className="text-blue-700 font-bold text-lg mb-2">{project.title}</div>
      <div className="text-gray-500 text-sm mb-2">{project.semester} / {project.author}</div>
      <p
        className="text-gray-800 mb-4"
        style={{
          display: '-webkit-box',
          WebkitBoxOrient: 'vertical',
          WebkitLineClamp: 2,
          overflow: 'hidden',
          textOverflow: 'ellipsis',
        }}
      >
        {project.description}
      </p>
      <div className="flex flex-wrap gap-2">
        {project.tags.map(tag => (
          <span key={tag} className="bg-purple-100 text-blue-700 text-xs font-semibold px-3 py-1 rounded-full">
            {tag}
          </span>
        ))}
      </div>

      {/* Delete Confirmation Modal */}
      <DeleteConfirmationModal
        isOpen={isModalOpen}
        onClose={() => setIsModalOpen(false)}
        onConfirm={handleDelete}
        projectTitle={project.title}
      />
    </div>
  );
}

export default ProjectCard;
