import React from 'react';
import { useNavigate } from 'react-router-dom';
import { toast } from 'react-toastify';

function Navbar() {
    const navigate = useNavigate();
    const username = localStorage.getItem('username');

    const handleLogout = () => {
        localStorage.removeItem('username');
        navigate('/login');
        toast.info('Logged out successfully.');
    };

    return (
        <nav className="bg-white shadow-md py-4 px-6 fixed top-0 left-0 right-0 z-10">
            <div className="flex justify-between items-center">
                {/* Website Name on the Left */}
                <div className="text-blue-700 font-bold text-xl">P-LIB</div>

                {/* User Info and Logout on the Right */}
                <div className="flex items-center gap-4">
                    <span className="text-gray-700 text-sm">Hello, {username}</span>
                    <button 
                        onClick={handleLogout}
                        className="text-red-600 text-sm hover:text-red-800 transition-colors"
                    >
                        Logout
                    </button>
                </div>
            </div>
        </nav>
    );
}

export default Navbar;
