import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../api/api';
import ProjectCard from '../components/ProjectCard';
import Layout from '../components/Layout';

function HomePage() {
  const [projects, setProjects] = useState([]);
  const [selectedSemester, setSelectedSemester] = useState('');
  const [selectedClass, setSelectedClass] = useState(''); // New state for Class filter
  const navigate = useNavigate();

  useEffect(() => {
    api.get('/projects')
      .then(response => {
        setProjects(response.data);
      })
      .catch(error => {
        console.error('Failed to fetch projects:', error);
      });
  }, []);

  // Extract unique semesters and classes from projects data
  const semesters = Array.from(new Set(projects.map(p => p.semester)));
  const classes = Array.from(new Set(projects.map(p => p.class)));

  const handleSemesterChange = (e) => {
    setSelectedSemester(e.target.value);
  };

  const handleClassChange = (e) => {
    setSelectedClass(e.target.value);
  };

  const handleCreateProjectClick = () => {
    navigate('/create-project');
  };

  // Filter projects based on selectedSemester and selectedClass
  const filteredProjects = projects.filter(project => {
    const semesterMatch = selectedSemester ? project.semester === selectedSemester : true;
    const classMatch = selectedClass ? project.class === selectedClass : true;
    return semesterMatch && classMatch;
  });

  return (
    <Layout>
      <div className="max-w-4xl mx-auto px-4 py-6">
        {/* Filter and Add Project Button */}
        <div className="flex flex-col md:flex-row justify-between items-center mb-6">
          {/* Filters */}
          <div className="flex flex-col sm:flex-row gap-4">
            {/* Semester Filter */}
            <div>
              <label htmlFor="semester" className="block text-gray-700 font-semibold mb-1">Semester</label>
              <select
                id="semester"
                className="border border-gray-300 rounded px-3 py-2 text-gray-700"
                value={selectedSemester}
                onChange={handleSemesterChange}
              >
                <option value="">All Semesters</option>
                {semesters.map((semester) => (
                  <option key={semester} value={semester}>
                    {semester}
                  </option>
                ))}
              </select>
            </div>

            {/* Class Filter */}
            <div>
              <label htmlFor="class" className="block text-gray-700 font-semibold mb-1">Class</label>
              <select
                id="class"
                className="border border-gray-300 rounded px-3 py-2 text-gray-700"
                value={selectedClass}
                onChange={handleClassChange}
              >
                <option value="">All Classes</option>
                {classes.map((cls) => (
                  <option key={cls} value={cls}>
                    {cls}
                  </option>
                ))}
              </select>
            </div>
          </div>

          {/* Add Project Button */}
          <div className="mt-4 md:mt-0">
            <button
              onClick={handleCreateProjectClick}
              className="text-white text-2xl font-bold w-12 h-12 flex items-center justify-center rounded-md bg-purple-600 hover:bg-purple-700 transition-colors"
              title="Add New Project"
            >
              +
            </button>
          </div>
        </div>

        {/* Projects List */}
        {filteredProjects.length > 0 ? (
          <div className="grid grid-cols-1 gap-6">
            {filteredProjects.map((project, index) => (
              <ProjectCard
                key={index}
                project={project}
              />
            ))}
          </div>
        ) : (
          <p className="text-center text-gray-500">No projects available.</p>
        )}

        {/* Pagination (Optional) */}
        <div className="flex justify-center gap-4 mt-8">
          <button className="text-blue-700 hover:underline">Previous</button>
          <button className="text-blue-700 hover:underline">Next</button>
        </div>
      </div>
    </Layout>
  );
}

export default HomePage;
