import React, { useState, useEffect } from 'react';
import api from '../api/api';
import { toast } from 'react-toastify';

function Navbar({ username }) {
  return (
    <div className="bg-white shadow-md py-4 px-6 mb-6">
      <div className="max-w-2xl mx-auto flex justify-between items-center">
        <div className="text-blue-700 font-bold text-xl">P-LIB</div>
        <div className="text-gray-700 text-sm">Hello, {username}</div>
      </div>
    </div>
  );
}

function ProjectCard({ title, date, description, tags, semester }) {
  return (
    <div className="bg-white shadow-md rounded-lg p-4 mb-6">
      <div className="text-blue-700 font-bold text-lg mb-2">{title}</div>
      <div className="text-gray-500 text-sm mb-2">{date}</div>
      {semester && <div className="text-blue-500 text-sm font-semibold mb-4">{semester}</div>}
      <p className="text-gray-800 mb-4">{description}</p>
      <div className="flex flex-wrap gap-2">
        {tags.map(tag => (
          <span key={tag} className="bg-purple-100 text-blue-700 text-xs font-semibold px-3 py-1 rounded-full">
            {tag}
          </span>
        ))}
      </div>
    </div>
  );
}

function HomePage() {
  const [projects, setProjects] = useState([]);
  const username = "John Doe";

  useEffect(() => {
    try {
      const response = api.get('/projects');
      setProjects(response.json());
    }
    catch (error) {
      toast.error(error.response.data.error);
      console.error(error);
    };
  }, []);

  return (
    <div className="bg-gray-100 min-h-screen">
      <Navbar username={username} />

      <div className="p-6">
        <div className="max-w-2xl mx-auto">
          {projects.length > 0 ? (
            projects.map((project, index) => (
              <ProjectCard
                key={index}
                title={project.title}
                date={project.date}
                semester={project.semester}
                description={project.description}
                tags={project.tags}
              />
            ))
          ) : (
            <p>No projects available.</p>
          )}
        </div>
        <div className="max-w-2xl mx-auto flex justify-between mt-4">
          <button className="text-blue-700">Previous</button>
          <button className="text-blue-700">Next</button>
        </div>
      </div>
    </div>
  );
}

export default HomePage;
