import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import api from '../api/api';
import { toast } from 'react-toastify';

function ProjectDetailPage() {
  const { id } = useParams();
  const [project, setProject] = useState(null);

  useEffect(() => {
    const fetchProject = async () => {
      try {
        const response = await api.get(`/projects/${id}`);
        setProject(response.data);
      } catch (error) {
        console.error('Failed to fetch project details:', error);
        const errorMessage = error.response?.data?.error || 'Failed to load project details.';
        toast.error(errorMessage);
      }
    };

    fetchProject();
  }, [id]);

  if (!project) {
    return <div className="min-h-screen flex items-center justify-center">Loading...</div>;
  }

  return (
    <div className="bg-gray-100 min-h-screen p-6">
      <div className="max-w-2xl mx-auto bg-white rounded-lg shadow-md p-6">
        <h1 className="text-blue-700 font-bold text-2xl mb-4">{project.title}</h1>
        <div className="text-gray-500 text-sm mb-4">
          {project.semester} / {project.author}
        </div>
        <p className="text-gray-800 mb-4">{project.description}</p>
        <div className="flex flex-wrap gap-2 mb-4">
          {project.tags.map(tag => (
            <span key={tag} className="bg-purple-100 text-blue-700 text-xs font-semibold px-3 py-1 rounded-full">
              {tag}
            </span>
          ))}
        </div>
      </div>
    </div>
  );
}

export default ProjectDetailPage;
